// productsSlice.ts
// -----------------------------------------------------------
// This slice stores the list of all products in Redux state.
// Since products are static and do not change, we just load
// them from productsData and keep them as the initial state.
// -----------------------------------------------------------

import { createSlice } from "@reduxjs/toolkit";
import { Products } from "./ProductsData"; // Import all products

// -----------------------------------------------------------
// üõçÔ∏è productsSlice
// -----------------------------------------------------------
// - name: "products" ‚Üí identifies the slice
// - initialState: Products (static product list)
// - reducers: {} ‚Üí no actions needed because products never change
// -----------------------------------------------------------

const productsSlice = createSlice({
  name: "products",
  initialState: Products,
  reducers: {}, // No reducers needed (read-only product list)
});

// Export the reducer so the store can use it
export default productsSlice.reducer;
